package com.example.android.bostontourguide;


import android.os.Bundle;
import android.support.v4.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ListView;

import java.util.ArrayList;

/**
 * A simple {@link Fragment} subclass.
 */
public class MuseumsFragment extends Fragment {

    private View rootView;
    private ArrayList<Place> placeArrayList = new ArrayList<>();
    public static final String ARG_PAGE = "ARG_PAGE";
    private int mPage;

    public static MuseumsFragment newInstance(int page) {
        Bundle args = new Bundle();
        args.putInt(ARG_PAGE, page);
        MuseumsFragment fragment = new MuseumsFragment();
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        mPage = getArguments().getInt(ARG_PAGE);
    }

    public MuseumsFragment() {
        // Required empty public constructor
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        rootView = inflater.inflate(R.layout.places_list, container, false);

        setupListView();

        ListView listView = (ListView) rootView.findViewById(R.id.list);
        PlaceAdapter placeAdapter = new PlaceAdapter(getContext(), placeArrayList);
        listView.setAdapter(placeAdapter);

        return rootView;
    }

    private void setupListView() {
        placeArrayList.add(new Place("Peabody Museum", "Archeology and Ethonology", "https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcQiJFncZE4ElwndK7mf_oRjuWwe0akRtwdVUcgnohz_uaLhtF_c"));
        placeArrayList.add(new Place("Museum of Comparative Zoology", null, "https://s-media-cache-ak0.pinimg.com/564x/56/49/95/564995884dd4a844297b605309a73a45.jpg"));
        placeArrayList.add(new Place("Museum of Fine Arts", "Artwork", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Isabella Stewart Gardner Museum", "Botany", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Museum of Science", null, "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("JFK Presidential Library and Museum", "Lirary and museum", "https://www.jfklibrary.org/~/media/assets/Audiovisual/Still%20Photographs/NLK%20Photographs%20Government%20-%20JFK%20Library/NLJFK%2093-C52-29.jpg"));
        placeArrayList.add(new Place("Commonwealth Museum", "State's museum", "http://www.sec.state.ma.us/mus/images/frontroof2.jpg"));
    }
}
