package com.example.android.bostontourguide;


import android.os.Bundle;
import android.support.v4.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ListView;

import java.util.ArrayList;

/**
 * A simple {@link Fragment} subclass.
 */
public class TheatersFragment extends Fragment {

    private View rootView;
    private ArrayList<Place> placeArrayList = new ArrayList<>();
    public static final String ARG_PAGE = "ARG_PAGE";
    private int mPage;

    public static TheatersFragment newInstance(int page) {
        Bundle args = new Bundle();
        args.putInt(ARG_PAGE, page);
        TheatersFragment fragment = new TheatersFragment();
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        mPage = getArguments().getInt(ARG_PAGE);
    }

    public TheatersFragment() {
        // Required empty public constructor
    }


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        rootView = inflater.inflate(R.layout.places_list, container, false);

        setupListView();

        ListView listView = (ListView) rootView.findViewById(R.id.list);
        PlaceAdapter placeAdapter = new PlaceAdapter(getContext(), placeArrayList);
        listView.setAdapter(placeAdapter);

        return rootView;
    }

    private void setupListView() {
        placeArrayList.add(new Place("Symphony Hall", "Music", "http://visitbirmingham.com/m/9ce7d1/600.jpg"));
        placeArrayList.add(new Place("Hatch Memorial Shell", "Outdoor theater", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Coolidge Corner Theatre", "Movie theater", "http://www.bostoncentral.com/ppix/activities399.jpg"));
        placeArrayList.add(new Place("Boston Opera House", "Performing arts", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Shubert Theater", "Performing arts", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Cutler Majestic Theater", "Performing arts", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Wilbur Theater", "Performing arts", "http://www.touristsbook.com/boston/files/2014/11/wilbur.jpeg"));
        placeArrayList.add(new Place("Brighton Music Hall", "Music", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Tanglewood Music Center", "Outdoor theater", "http://www.stephanedeneve.com/wp-content/uploads/2012/07/tanglewood-music-festival.jpg"));
        placeArrayList.add(new Place("Wang Theater", "Performing arts", "http://www.geoffreygoldbergphotography.com/wp-content/uploads/2013/04/Buffalo-Shea-Pac.jpg"));
        placeArrayList.add(new Place("Orpheum Theater", "Performing arts", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Somerville Theater", "Movie theater", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("Landmark Kendall Square", "Movie theater", "data:image/jpeg;base64,"));
        placeArrayList.add(new Place("AMD Loews Boston Common", "Movie theater", "https://igx.4sqi.net/img/general/200x200/1202494_jfn8bAXa9FePZE_AqzCNoDROFVxiYRZHbQENbZ9SjOM.jpg"));
    }
}
